function isLegalBishop(move, board) {
  let deltaAlf = Math.abs(move.start.alf - move.end.alf);
  let deltaNum = Math.abs(move.start.num - move.end.num);
  let dirAlf = Math.sign(move.end.alf - move.start.alf);
  let dirNum = Math.sign(move.end.num - move.start.num);
  if (deltaAlf !== deltaNum) {
    return false;
  }
  //check if there are pieces on the way
  for (let i = 1; i < deltaAlf; i++) {
    if (board.hasPiece(move.start.num + i*dirNum, move.start.alf + i*dirAlf)) {
      return false;
    }
  }
  return true;
}

// export { isLegalBishop };
module.exports = isLegalBishop;
