function isLegalKnight(move) {
  return Math.abs(move.start.alf - move.end.alf) === 1 &&
        Math.abs(move.start.num - move.end.num) === 2 ||
        Math.abs(move.start.alf - move.end.alf) === 2 &&
        Math.abs(move.start.num - move.end.num) === 1;
}

// export { isLegalKnight };
module.exports = isLegalKnight;
