function getPieceSrc(nc) {
  //returns the upper left-hand corner coordinate of the piece's image
  let sz = 1000 / 3;
  switch (nc) {
    case "K": return {x: 0, y: 0};
    case "Q": return {x: sz, y: 0};
    case "B": return {x: 2 * sz, y: 0};
    case "N": return {x: 3 * sz, y: 0};
    case "R": return {x: 4 * sz, y: 0};
    case "P": return {x: 5 * sz, y: 0};
    case "k": return {x: 0, y: sz};
    case "q": return {x: sz, y: sz};
    case "b": return {x: 2 * sz, y: sz};
    case "n": return {x: 3 * sz, y: sz};
    case "r": return {x: 4 * sz, y: sz};
    case "p": return {x: 5 * sz, y: sz};
    default: return null;
  }
}

// export { getPieceSrc };
module.exports.getPieceSrc = getPieceSrc;
