class Utils {
  static empty8x8() {
    // return an empty board entity
    let a = new Array(8);
    for (let i = 0; i < 8; i++) {
      a[i] = new Array(8);
    }
    return a;
  }

  static isUpperCase(str) {
    return str === str.toUpperCase();
  }

  static color(piece) {
    // if (piece === undefined) throw "no piece provided";
    if (piece === undefined) return undefined;
    // true for white; false for black
    return Utils.isUpperCase(piece);
  }
}

module.exports = Utils;
